package com.kalem.authenticationserver.basemodels;import com.fasterxml.jackson.annotation.JsonIgnore;import com.fasterxml.jackson.databind.PropertyNamingStrategy;import com.fasterxml.jackson.databind.annotation.JsonNaming;import com.kalem.authenticationserver.basemodels.user.UserDto;import com.kalem.sharedclass.enums.DeletedType;import lombok.Getter;import lombok.Setter;import javax.persistence.MappedSuperclass;import java.io.Serializable;import java.util.Date;@Getter@Setter@MappedSuperclass@JsonNaming( PropertyNamingStrategy.SnakeCaseStrategy.class )public abstract class BaseDto implements Serializable {    private I id;    @JsonIgnore    private Date createdDate;    @JsonIgnore    private Date modifiedDate;    @JsonIgnore    private UserDto createdUser;    @JsonIgnore    private UserDto modifiedUser;    @JsonIgnore    private DeletedType deleted = DeletedType.FALSE;    }