package com.kalem.authenticationserver.basemodels;import com.fasterxml.jackson.annotation.JsonIgnore;import com.kalem.authenticationserver.basemodels.user.UserDto;import lombok.Getter;import lombok.Setter;import javax.persistence.MappedSuperclass;import java.time.LocalDateTime;@Getter@Setter@MappedSuperclasspublic abstract class BaseDto<I extends Number> extends CoreDto {    private I ID;    private LocalDateTime createdDate;    private LocalDateTime modifiedDate;    private UserDto createdUser;    private UserDto modifiedUser;    private DeletedType deleted = DeletedType.FALSE;}