package com.kalem.authenticationserver.basemodels.provider;import com.kalem.sharedclass.basemodels.CreatedByDto;import com.thoughtworks.xstream.annotations.XStreamAlias;import lombok.AllArgsConstructor;import lombok.Builder;import lombok.Data;import lombok.NoArgsConstructor;import org.springframework.security.core.GrantedAuthority;import org.springframework.security.core.authority.SimpleGrantedAuthority;import java.util.ArrayList;import java.util.Collection;@Data@Builder@AllArgsConstructor@NoArgsConstructor@XStreamAlias( "provider" )public class ProviderDto extends CreatedByDto<Integer> {    private String name;    private String password;    private String description;    private Collection<ProviderRoleDto> roles;    public Collection<GrantedAuthority> getAuthorities() {        Collection<GrantedAuthority> authorities = new ArrayList<GrantedAuthority>();        if ( roles != null )            for ( ProviderRoleDto role : roles ) {                authorities.add( new SimpleGrantedAuthority( role.getName() ) );            }        return authorities;    }}